import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, NavigationExtras, Router } from '@angular/router';
import { AlertController } from '@ionic/angular';

@Component({
  selector: 'app-ingresar-ingredientes',
  templateUrl: './ingresar-ingredientes.page.html',
  styleUrls: ['./ingresar-ingredientes.page.scss'],
})
export class IngresarIngredientesPage implements OnInit {
  usuarioRecibido: string='';
  passwordRecibido: string='';
  nombrePreparacion: string='';
  apellido: string='';
  mensaje: string= '';
  selectedOption: string='';
  selectedDate: string='';
  usuarios: any = [];

  constructor(private router:Router, private activatedRouter:ActivatedRoute, private alertController: AlertController) {
    this.activatedRouter.queryParams.subscribe(params => {
      if(this.router.getCurrentNavigation()?.extras?.state) {
        this.usuarioRecibido = this.router.getCurrentNavigation()?.extras?.state?.['usuarioEnviado'];
        this.passwordRecibido = this.router.getCurrentNavigation()?.extras?.state?.['passwordEnviado'];

        console.log();
      }
    })
  }

  async presentAlert(message: string) {
    const alert = await this.alertController.create({
      header: 'Mensaje',
      message: message,
      buttons: ['OK']
    });

    await alert.present();
  }
  ngOnInit() {
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>ingresar-ingredientes</ion-title>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
      <ion-back-button></ion-back-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>
<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="medium">ingresar-ingredientes</ion-title>
    </ion-toolbar>
  </ion-header>
  <div id="container" class="cuerpo-page">
    <ion-card>
      <ion-card-content>
        <ion-card-title>
          <h1>Ingresa los ingredientes</h1>
          <!-- 
              <p>Usuario: {{ usuarioRecibido }}</p>
          -->
          
        </ion-card-title>
      
        <ion-item>
          <ion-label position="floating">Ingrediente</ion-label>
          <ion-input type="text" [(ngModel)]="nombrePreparacion"></ion-input>
        </ion-item>
        <!-- 
          <ion-item>
          <ion-label position="floating">Apellido</ion-label>
          <ion-input type="text" [(ngModel)]="apellido"></ion-input>
        </ion-item>
        -->
           
        <ion-item>
          <ion-label>Seleccione Ingrediente</ion-label>
          <ion-select [(ngModel)]="selectedOption">
            <ion-select-option value="opcion1">Mantequilla</ion-select-option>
            <ion-select-option value="option2">Azúcar</ion-select-option>
            <ion-select-option value="option3">Vainilla</ion-select-option>
            <ion-select-option value="option4">Harina</ion-select-option>
            <ion-select-option value="option5">Huevos</ion-select-option>
          </ion-select>
        </ion-item>
        <!--
        <ion-item>
          <ion-label>Fecha Nacimiento</ion-label>
          <ion-input type="date" [(ngModel)]="selectedDate"></ion-input>
        </ion-item>
        -->
       
         
      </ion-card-content>
      <!--Creamos el botón
      <ion-button (click)="limpiar()">Limpiar</ion-button>
      <ion-button shape="block" (click)="registrar()">Registro: {{ mensaje }}</ion-button>
      -->
    </ion-card>  
  </div>
</ion-content>
