import { Component } from '@angular/core';
import { ActivatedRoute, NavigationExtras, Router } from '@angular/router';
import { AlertController } from '@ionic/angular';


@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {

  usuarioRecibido: string='';
  passwordRecibido: string='';
  nombre: string='';
  apellido: string='';
  mensaje: string= '';
  /*selectedOption: string='';
  selectedDate: string='';*/
  usuarios: any = [];

  constructor(private router:Router, private activatedRouter:ActivatedRoute, private alertController: AlertController) {
    this.activatedRouter.queryParams.subscribe(params => {
      if(this.router.getCurrentNavigation()?.extras?.state) {
        this.usuarioRecibido = this.router.getCurrentNavigation()?.extras?.state?.['usuarioEnviado'];
        this.passwordRecibido = this.router.getCurrentNavigation()?.extras?.state?.['passwordEnviado'];

        console.log();
      }
    })
  }

  async presentAlert(message: string) {
    const alert = await this.alertController.create({
      header: 'Mensaje',
      message: message,
      buttons: ['OK']
    });

    await alert.present();
  }

  registrar() {
    if (this.nombre.trim() === '' || this.apellido.trim() === '') {
      this.presentAlert('Error: nombre y apellido vacios');
      this.limpiar();
    } else {

      // Lógica para manejar el envío del formulario cuando es válido
      //this.router.navigate(['/menu-ppal'])
      this.mensaje = "usuario registrado";
    }

  }

  limpiar() {
    this.nombre = '';
    this.apellido = '';
    this.mensaje = '';
    /*this.selectedOption = '';
    this.selectedDate = '';*/
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title class="titulo">
      Página de Registro
    </ion-title>
    <ion-buttons>
      <ion-back-button></ion-back-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="medium">Página de Registro</ion-title>
    </ion-toolbar>
  </ion-header>

  <div id="container" class="cuerpo-page">
    <ion-card>
      <ion-card-content>
        <ion-card-title>
          <h1>Ingresa tus datos</h1>
          <!-- 
              <p>Usuario: {{ usuarioRecibido }}</p>
          -->

        </ion-card-title>

        <ion-item>
          <ion-label position="floating">Nombre</ion-label>
          <ion-input type="text" [(ngModel)]="nombre"></ion-input>
        </ion-item>

        <ion-item>
          <ion-label position="floating">Apellido</ion-label>
          <ion-input type="text" [(ngModel)]="apellido"></ion-input>
        </ion-item>
        <!-- 
        <ion-item>
          <ion-label>Nivel de Educación</ion-label>
          <ion-select [(ngModel)]="selectedOption">
            <ion-select-option value="option1">Universitario</ion-select-option>
            <ion-select-option value="option2">Magister</ion-select-option>
            <ion-select-option value="option3">Doctorado</ion-select-option>
          </ion-select>
        </ion-item>
        <ion-item>
          <ion-label>Fecha Nacimiento</ion-label>
          <ion-input type="date" [(ngModel)]="selectedDate"></ion-input>
        </ion-item>
         -->
      </ion-card-content>
      <!--Creamos el botón-->
      <ion-button (click)="limpiar()">Limpiar</ion-button>
      <ion-button shape="block" (click)="registrar()">Registro: {{ mensaje }}</ion-button>

    </ion-card>

  </div>
</ion-content>
