import { Component, OnInit } from '@angular/core';
import { NavigationExtras, Router } from '@angular/router';
import { AlertController } from '@ionic/angular';

@Component({
  selector: 'app-login',
  templateUrl: './login.page.html',
  styleUrls: ['./login.page.scss'],
})
export class LoginPage implements OnInit {

  usuario: string="";
  password: string=""
  constructor(private alertController:AlertController, private router:Router) { }

  ngOnInit() {
  }

  login() {
    if (this.usuario.trim() == 'javiera' && this.password.trim() == '4321') {
      let navigationExtras: NavigationExtras = {
        state: {
          usuarioEnviado: this.usuario,
          passwordEnviada: this.password

        }
      }
      this.router.navigate(['/menu-ppal'], navigationExtras);
    }
    else {
      this.presentAlert('Password ncorrecta');

    }
  }
   
  registro() {
    this.router.navigate(['/home'])
  }
  async presentAlert(message: string) {
      const alert = await this.alertController.create({
        header: 'Mensaje',
        message: message,
        buttons: ['OK']
      });
      await alert.present();
 }
      
 


}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>Convertidor de Recetas</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="medium">Convertidor de Recetas</ion-title>
    </ion-toolbar>
  </ion-header>
  <div class="ion-text-center cuerpo-page">
    <!-- Colocamos todo dentro de un ion-card -->
    <ion-card>
      <ion-card-title>
        <h1>Bienvenidos</h1>
      </ion-card-title>

      <!-- Colocamos la imagen dentro de un ion-grid-->
      <ion-grid>
        <ion-row>
          <ion-col size="12" class="ion-text-center">
            <img class="logo" src="assets/Tortas.png" alt="Imagen de Tortas">
          </ion-col>
        </ion-row>
      </ion-grid>

      <!--Creamos los campos para el login-->
    <ion-card-content>

      <ion-item>
        <ion-label position="floating">Usuario</ion-label>
        <ion-input type="text" maxlength="8" minlength="3" [(ngModel)]="usuario"></ion-input>
      </ion-item>

      <ion-item>
        <ion-label position="floating">Password</ion-label>
        <ion-input type="Password" maxlength="4" [(ngModel)]="password"></ion-input>
      </ion-item>
    </ion-card-content>
      <!--Creamos el botón-->
      <ion-button expand="block" (click)="login()">Ingrese ahora</ion-button>
      <ion-label>No estás registrado? -->      Registrate aquí </ion-label>
      <ion-button (click)="registro()">Registro</ion-button>
    </ion-card>
  </div>
</ion-content>
